-- ----------------------------
-- Table structure for `article`
-- ----------------------------
DROP TABLE IF EXISTS `article`;
CREATE TABLE `article` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(255) DEFAULT NULL,
  `uid` varchar(20) DEFAULT NULL,
  `comment` varchar(3000) DEFAULT NULL,
  `commitTime` varchar(200) DEFAULT NULL,
  `cNum` int(11) DEFAULT NULL COMMENT '评论数量',
  `pNum` int(11) DEFAULT NULL COMMENT '点赞数',
  `pic1` varchar(200) DEFAULT NULL,
  `pic2` varchar(200) DEFAULT NULL,
  `pic3` varchar(200) DEFAULT NULL,
  `hot` float(11,6) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=3 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of article
-- ----------------------------
INSERT INTO `article` VALUES ('1', '拥有诗意的心态,才能拥有诗意的生活', '3', '年前，总记得父亲有个宽厚的肩膀，小小的自己跨坐在上面，越过人山人海去看更广阔的天空，那个时候期望自己有一双翅膀，能够像鸟儿一样飞得高，看得远。虽然父亲有时会和自己开玩笑，但在做错事的时候会受到严厉的训斥。父亲有双粗糙的大手掌，手把手教我走路、骑车，却会在该放手的时刻果断地放开让自己去大胆尝试，那个时候期望快快长大，能够做自己想做的事，不用受父亲的“控制”。父亲是智慧树，他无所不知、无所不晓，虽然你有十万个为什么，但是也难不倒他。', '2019-01-08 12:12:01', '1000', '1081', null, null, null, '2611.000000');
INSERT INTO `article` VALUES ('2', '长这么大，有没有哪一瞬间真的被感动到的？', '3', '现在的我，出差在普宁，做新店开业帮扶。<br/>普宁每家每户都会有一套茶具，因为他们酷爱喝茶，就连装修的工人也会带一个茶壶到工地，对于喜欢喝茶的我来说，刚到普宁便第一眼就喜欢上了这里。<br/>原本，这时的我，不应该坐在电脑面前码字的，领导，请允许我偷一下小懒。<br/>一个人的感动，可以因为一点很小很小的事情，就感动的一塌糊涂。是的，今天的我，就是一件很小很小的事情，被感动的热泪盈眶。<br/>新店的老板是一个三十岁出头的女老板，长得小巧的模样，给人一种江南水乡气质的感觉，而且泡的一手好茶。老板娘不仅会泡茶，而且还会做生意，注意小细节。一个顾客，她从店面接待进来，到顾客走出店面，她都会一路跟着，如果顾客要走了，她会送到顾客上车，直到走远，她才会回到店里面。这对我来说，无疑上了一大节礼仪课，受益匪浅。<br/>其实，今天发生的事情，对于老板娘来说，真的可能是一件很小的事情，仅仅只是举手之劳而已，可是对于受益者，确实很激动的受惠吧。<br/>清洁阿叔，在店面门口清洁完准备走的时候，老板娘到店里拿了一个送给顾客的进店礼，保温杯。老板娘当时只说了一句话，他天天帮我扫门口挺辛苦的。当然，这时候清洁阿叔已经走远了，可是，我依然记得，阿叔眼中感激的眼神。<br/>如今的社会，我不知道还有多少人，会记得去感激，又有多少人，会知道要去感谢？现在新闻上出现的大多数可能是什么某某地方，又有某某某被勒索，被反诬。可是我知道，这个世界上，还有最纯真的善良。如果，我们每个人看人看是都能美好一些，我相信这个世界终会被美丽包围。<br/>现在的我，泪水还在眼中徘徊，不知是感动，还是羞愧。感动与老板娘的做法，羞愧于我也见过这个阿叔很多面了，做的却只是把垃圾丢到他拉着的垃圾车里面，却没有想到他的辛苦。<br/>职业不分高低贵贱，可是人格人品却有好坏之分，我一直都相信只要心存善念，就一定会有好的收获。不必去争、不必去抢，就淡淡的做着自己该做的事情。虽然我不是一个特别有善心的人，可是我希望我能做到不害人，做好自己便好。', '2019-01-02 00:00:00', '1', '2', '/other/Author/gandong.jpg', null, null, '0.105412');

