-- ----------------------------
-- Procedure structure for `hacknews`
-- ----------------------------
DROP PROCEDURE IF EXISTS `hacknews`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `hacknews`()
UPDATE article set hot = (pNum+cNum*1.5)/POW(TO_DAYS(NOW())-TO_DAYS(commitTime),1.8)
;;
DELIMITER ;

-- ----------------------------
-- Event structure for `uph`
-- ----------------------------
DROP EVENT IF EXISTS `uph`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` EVENT `uph` ON SCHEDULE EVERY 2 HOUR STARTS '2019-01-09 00:00:00' ON COMPLETION PRESERVE ENABLE DO CALL hacknews()
;;
DELIMITER ;
